    const numberLower = passwordLength - (numberUpper + numberNumeric + numberSpecial);
                 
    let upperChars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';
    let lowerChars = 'abcdefghijklmnopqrstuvwxyz';
    let numberChars = '0123456789';
